<template>
<div>
<div class="sidenav">
         <div class="login-main-text">
           <div class="w-75"><img src="@/assets/logo.svg" class=".img-fluid"></div>
           <div class="col-xs-12" style="height:50px;"></div>
            <h2>User Interface for Data Visualisation</h2>
            <p>Login here to have access.</p>
         </div>
</div>

      <div class="main">
         <div class="col-md-6 col-sm-12">
            <div class="login-form">
               <form @submit.prevent="handleSubmit">
                  <div class="form-group">
                     <label>Username</label>
                     <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" placeholder="Username" />
                     <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
                  </div>
                  <div class="form-group">
                     <label>Password</label>
                     <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" placeholder="Passoword" />
                     <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
                  </div>
                  <button class="btn btn-primary" :disabled="loggingIn">Login</button>
                  <img v-show="loggingIn" src="data:image/gif;base64," />
               </form>
                  <br>
                  <div v-if="alert.message" :class="`alert ${alert.type}`">{{alert.message}}</div>
            </div>
         </div>
      </div>      
</div>
</template>

<script>
export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        loggingIn () {
            return this.$store.state.authentication.status.loggingIn;
        },
        alert () {
            return this.$store.state.alert
        }
    },
    created () {
        // reset login status
        this.$store.dispatch('authentication/logout');
    },
    methods: {
        handleSubmit (e) {
            this.submitted = true;
            const { username, password } = this;
            const { dispatch } = this.$store;
            if (username && password) {
                dispatch('authentication/login', { username, password });
            }
        }
    }
};
</script>

